function Get-DefaultMSAToken {
    <#
    .SYNOPSIS
    Returns the default/fallback MSA Device Token

    .DESCRIPTION
    Returns the generic fallback MSA Device Token that is used when the authentic
    token cannot be retrieved from the system. This is the same token used by
    Get-DeviceMSAToken as a fallback.

    .EXAMPLE
    $defaultToken = Get-DefaultMSAToken

    .EXAMPLE
    # Compare with cached token
    $cachedToken = Get-Content "$env:ProgramData\StoreLib\MSAToken.dat" -Raw
    if ($cachedToken -eq (Get-DefaultMSAToken)) {
        Write-Host "Using fallback token"
    }

    .NOTES
        Author  : LoÃ¯c Ade
        Version : 1.0.0

        This token is a valid generic MSA token that works for basic Store API access,
        but may have limitations compared to a device-specific token extracted from
        the Windows registry.
    #>

    [CmdletBinding()]
    param()

    # Return the generic fallback MSA Device Token
    # This is the same token used in Get-DeviceMSAToken at line 250
    return '<Device></Device>'
}
